mapper.select:

SELECT COUNT(*) CNT
  FROM STAT_POPULATION_TREND_SIDO A
WHERE A.YYYYMM = #{yyyymm}


SELECT COUNT(*) CNT
  FROM STAT_POPULATION_MOVE_SIDO A
WHERE A.YYYYMM = #{yyyymm}

